async def namedata(country):
    countries = await alldata()
    for i in countries:
        if i[0] == country:
            return i[0]

async def sizedata(country):
    countries = await alldata()
    for i in countries:
        if i[0] == country:
            return i[1]

async def flagdata(country):
    countries = await alldata()
    for i in countries:
        if i[0] == country:
            return i[2]

